import { UPDATEARRAYS } from "../actions/tasksArraysAction";

const updateTasksArraysReducer = (state = {}, action) => {
  switch (action.type) {
    case UPDATEARRAYS:
      return { ...action.payload };
      break;

    default:
      return state;
      break;
  }
};

export default updateTasksArraysReducer;
