import React, { useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import TaskColorPicker from "./TaskColorPicker";
import TaskStatusSelect from "./TaskStatusSelect";
import TaskUrgencySelect from "./TaskUrgencySelect";

import { updateArraysAction } from "../../../state/actions/updateArraysAction";

const TaskCreation = ({ setPopupIsOpen }) => {
  const [colorway, setColorway] = useState("black");
  const [status, setStatus] = useState("todo");
  const [urgency, setUrgency] = useState("low");

  const tasksArrays = useSelector(state => state.updateArraysReducer);

  const [tasksCounter, setTasksCounter] = useState(tasksArrays.tasksCounter);

  const dispatch = useDispatch();

  const todoArray = tasksArrays.todoArray;
  const inProgressArray = tasksArrays.inProgressArray;
  const completedTasksArray = tasksArrays.completedTasksArray;
  let getTasksCounter = tasksArrays.tasksCounter;

  const updatingArraysInDataBase = async newTasksCounter => {
    console.log("newTasksCounter", newTasksCounter);

    const updatedArrays = {
      todoArray,
      inProgressArray,
      completedTasksArray,
      tasksCounter: newTasksCounter
    };

    dispatch(updateArraysAction(updatedArrays));

    console.log("updatedArrays", updatedArrays);

    const putMethod = {
      method: "PUT",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
        "auth-token": localStorage.getItem("token")
      },
      body: JSON.stringify(updatedArrays)
    };

    await fetch(
      `https://tracker-database.youngwebdevs.now.sh/dashboard/updatingarrays`,
      putMethod
    );

    console.log("PUT ENDED");
  };

  const createTask = e => {
    e.preventDefault();
    setPopupIsOpen(false);

    console.log(getTasksCounter, "tasksCounter before");
    const newTasksCounter = getTasksCounter + 1;
    console.log(newTasksCounter, "newTasksCounter after");

    const title = e.target[0].value;
    const description = e.target[1].value;
    const deadlineISO = e.target[2].value;
    const dateAndTimeArray = deadlineISO.split("T");
    const date = dateAndTimeArray[0];
    console.log("date", date);
    const dateArray = date.split(":");
    const reducer = (accumulator, currentValue) => accumulator + currentValue;
    // console.log("date", date.reduce(reducer));
    const timeArray = dateAndTimeArray[1];
    console.log("timeArray", timeArray);
    const deadline = String(new Date(deadlineISO));
    const deadlineArray = deadline.split(" ");
    const dayText = deadlineArray[0];
    const monthText = deadlineArray[1];
    const dayNumber = deadlineArray[2];
    const year = deadlineArray[3];
    const time = deadlineArray[4].slice(0, 5);

    const taskObject = {
      id: newTasksCounter,
      title,
      description,
      dayText,
      monthText,
      dayNumber,
      year,
      time,
      colorway,
      status,
      urgency,
      deadlineISO
    };

    console.log(taskObject, "taskObject");

    switch (status) {
      case "todo":
        todoArray.push(taskObject);
        updatingArraysInDataBase(newTasksCounter);
        break;
      case "inProgress":
        inProgressArray.push(taskObject);
        updatingArraysInDataBase(newTasksCounter);
        break;
      case "done":
        completedTasksArray.push(taskObject);
        updatingArraysInDataBase(newTasksCounter);
        break;
      default:
        break;
    }
  };

  return (
    <Container>
      <TaskForm onSubmit={createTask}>
        <TitleInput type="text" placeholder="Task Title..." />
        <DescrptionInput placeholder="Task Descrption..." />
        <DeadlineInput type="datetime-local" max="3000-01-01T00:00" />

        <TaskColorPicker colorway={colorway} setColorway={setColorway} />
        <TaskStatusSelect status={status} setStatus={setStatus} />
        <TaskUrgencySelect urgency={urgency} setUrgency={setUrgency} />

        <SubmitButton type="submit">Add Deadline</SubmitButton>
      </TaskForm>
    </Container>
  );
};

export default TaskCreation;

const Container = styled.div`
  width: 100%;
  height: 100%;
  padding: 20px 0;
  background-color: ${c.shyBlue};
  border-radius: 15px;
  box-shadow: -6px -6px 20px 0 rgba(242, 248, 250, 0.9),
    6px 6px 16px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
`;

const TaskForm = styled.form`
  ${flexbox({ dir: "column", jc: "space-around" })}
  flex-wrap: wrap;
  height: 100%;
`;

const TitleInput = styled.input`
  flex-basis: 10%;
  height: 10%;
  width: 90%;

  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.25),
    inset -2px -2px 8px rgba(242, 248, 250, 0.9);
  padding: 20px;

  font-size: 1.4rem;
`;

const DescrptionInput = styled.textarea`
  resize: none;
  flex-basis: 30%;
  height: 30%;
  width: 90%;

  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.25),
    inset -2px -2px 8px rgba(242, 248, 250, 0.9);
  padding: 20px;

  font-size: 1.1rem;

  @media (max-width: 450px) {
    flex-basis: 25%;
    height: 25%;
  }
`;

const DeadlineInput = styled.input`
  flex-basis: 10%;
  height: 10%;
  width: 90%;
  text-align: center;

  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.25),
    inset -2px -2px 8px rgba(242, 248, 250, 0.9);

  font-size: 1.4rem;
`;

const SubmitButton = styled.button`
  height: 50px;
  width: 50%;
  min-width: 250px;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);
  border: none;
  background: ${c.shyBlue};
  font-size: 0.9rem;
  color: ${c.darkBlueText};
  text-shadow: 0 0 1px white;
  opacity: 0.8;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 1;
    cursor: pointer;
    background: ${c.lightBlue};
  }
`;
